/* Modal Styles */
.modal-overlay {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.5);
    z-index: 2000;
    display: flex;
    justify-content: center;
    align-items: center;
    backdrop-filter: blur(2px);
    animation: fadeIn 0.3s;
}

.modal-content {
    background: #fff;
    width: 80%;
    max-width: 800px;
    height: 80%;
    border-radius: 12px;
    display: flex;
    flex-direction: column;
    box-shadow: 0 10px 25px rgba(0,0,0,0.2);
    animation: modalPop 0.3s cubic-bezier(0.18, 0.89, 0.32, 1.28);
}

@keyframes modalPop {
    from { opacity: 0; transform: scale(0.9); }
    to { opacity: 1; transform: scale(1); }
}

.modal-header {
    padding: 16px 24px;
    border-bottom: 1px solid #ebebeb;
    display: flex;
    justify-content: space-between;
    align-items: center;
    background: #fff;
    border-radius: 12px 12px 0 0;
}

.modal-header h3 {
    margin: 0;
    font-size: 1.1rem;
    font-weight: 600;
    color: #333;
}

.modal-header button {
    background: transparent;
    border: none;
    font-size: 24px;
    line-height: 1;
    cursor: pointer;
    color: #999;
    padding: 0 8px;
    transition: color 0.2s;
}

.modal-header button:hover {
    color: #333;
}

.modal-body {
    flex: 1;
    overflow: hidden; /* Inner scroll */
    padding: 0;
    background-color: #f9f9f9;
    position: relative;
    border-bottom-left-radius: 12px;
    border-bottom-right-radius: 12px;
}

/* Code container specific */
.json-viewer {
    margin: 0;
    padding: 20px;
    font-family: 'Consolas', 'Monaco', 'Courier New', monospace;
    font-size: 14px;
    color: #333;
    white-space: pre-wrap;
    word-break: break-all;
    height: 100%;
    overflow-y: auto;
    box-sizing: border-box;
}

/* Sources Styling */
.meta-sources {
    margin-top: 15px;
    padding-top: 10px;
    border-top: 1px dashed #eee;
}

.meta-sources strong {
    display: block;
    margin-bottom: 8px;
    font-size: 0.9em;
    color: #555;
}

.meta-sources ul {
    list-style: none;
    padding: 0;
    margin: 0;
}

.meta-sources li {
    margin-bottom: 6px;
    line-height: 1.5;
}

.source-link, .history-source-link {
    color: #2196f3;
    text-decoration: none;
    font-size: 0.9em;
    display: inline-flex;
    align-items: center;
    gap: 5px;
    transition: color 0.2s;
}

.source-link:before, .history-source-link:before {
    content: 'üìÑ'; /* Icon */
    font-size: 1.1em;
}

.source-link[data-type="web"]:before, .history-source-link[data-type="web"]:before {
    content: 'üåê'; /* Web Icon */
}

.source-link:hover, .history-source-link:hover {
    text-decoration: underline;
    color: #1565c0;
}

/* Ensure meta container is visible */
.ai-meta {
    animation: fadeIn 0.5s ease;
}
